trigger_prototype.expression:
{6.2 APC UPS Symmetra LX by SNMP:phase.input.voltage[upsPhaseInputVoltage.1.1.{#PHASEINDEX}].last( {$TIME.PERIOD})} > {$UPS.INPUT_VOLT.MAX.WARN} 
or
{6.2 APC UPS Symmetra LX by SNMP:phase.input.voltage[upsPhaseInputVoltage.1.1.{#PHASEINDEX}].last( {$TIME.PERIOD})} < {$UPS.INPUT_VOLT.MIN.WARN}

{6.2 APC UPS Symmetra LX by SNMP:phase.output.voltage[upsPhaseOutputVoltage.1.1.{#PHASEINDEX}].last({$TIME.PERIOD})} > {$UPS.INPUT_VOLT.MAX.WARN}
or
{6.2 APC UPS Symmetra LX by SNMP:phase.output.voltage[upsPhaseOutputVoltage.1.1.{#PHASEINDEX}].last({$TIME.PERIOD})} < {$UPS.INPUT_VOLT.MIN.WARN}

trigger.expression:
{6.2 APC UPS Symmetra LX by SNMP:system.name[sysName.0].last(#1)} <> {6.2 APC UPS Symmetra LX by SNMP:system.name[sysName.0].last(#2)}
and
{6.2 APC UPS Symmetra LX by SNMP:system.name[sysName.0].strlen()}>0

{6.2 APC UPS Symmetra LX by SNMP:input.frequency[upsHighPrecInputFrequency].min({$TIME.PERIOD})}>0

and

(
{6.2 APC UPS Symmetra LX by SNMP:input.frequency[upsHighPrecInputFrequency].min({$TIME.PERIOD})} > {$UPS.INPUT_FREQ.MAX.WARN}
or
{6.2 APC UPS Symmetra LX by SNMP:input.frequency[upsHighPrecInputFrequency].max({$TIME.PERIOD})} < {$UPS.INPUT_FREQ.MIN.WARN}
)

{6.2 APC UPS Symmetra LX by SNMP:input.voltage[upsHighPrecInputLineVoltage].min({$TIME.PERIOD})} > 0 

and

(
{6.2 APC UPS Symmetra LX by SNMP:input.voltage[upsHighPrecInputLineVoltage].min({$TIME.PERIOD})} > {$UPS.INPUT_VOLT.MAX.WARN}
or
{6.2 APC UPS Symmetra LX by SNMP:input.voltage[upsHighPrecInputLineVoltage].max({$TIME.PERIOD})} < {$UPS.INPUT_VOLT.MIN.WARN}
)
